// === FRONTEND: Game.js ===
import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import { useNavigate } from "react-router-dom";
import "./Game.css";

const socket = io("http://localhost:5000");

const Game = () => {
  const [digits, setDigits] = useState([]);
  const [solution, setSolution] = useState("");
  const [gameStarted, setGameStarted] = useState(false);
  const [message, setMessage] = useState("");
  const [waiting, setWaiting] = useState(false);
  const [duelMode, setDuelMode] = useState(false);
  const [username, setUsername] = useState("");
  const [opponentName, setOpponentName] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    setUsername(storedUsername || "Guest");

    socket.on("game-started", (data) => {
      setDigits(data);
      setGameStarted(true);
      setSolution("");
      setMessage("");
      setDuelMode(false);
    });

    socket.on("solution-validated", (result) => {
      setMessage(result);
    });

    socket.on("waiting-for-opponent", () => {
      setWaiting(true);
      setMessage("Waiting for an opponent...");
    });

    socket.on("duel-started", ({ digits, opponent }) => {
      setDigits(digits);
      setGameStarted(true);
      setDuelMode(true);
      setWaiting(false);
      setMessage("Duel Started! Solve the challenge first!");
      setOpponentName(opponent);
    });

    socket.on("duel-cancelled", () => {
      setWaiting(false);
      setMessage("Duel cancelled.");
    });

    socket.on("duel-result", (result) => {
      setMessage(result);
      setDuelMode(false);
    });

    return () => {
      socket.off("game-started");
      socket.off("solution-validated");
      socket.off("waiting-for-opponent");
      socket.off("duel-started");
      socket.off("duel-cancelled");
      socket.off("duel-result");
    };
  }, []);

  const startGame = () => {
    socket.emit("start-game", { username });
  };

  const startDuel = () => {
    socket.emit("start-duel", { username });
    setWaiting(true);
    setMessage("Searching for an opponent...");
  };

  const cancelDuel = () => {
    socket.emit("cancel-duel");
    setWaiting(false);
    setMessage("Duel cancelled.");
  };

  const submitSolution = () => {
    if (!solution.trim()) {
      setMessage("Please enter a solution first!");
      return;
    }
    const uid = localStorage.getItem("uid");
    socket.emit("submit-solution", { solution, duelMode, uid });
  };

  const clearSolution = () => {
    setSolution("");
  };

  const returnToMenu = () => {
    setGameStarted(false);
    setDigits([]);
    setSolution("");
    setMessage("");
    setWaiting(false);
    setDuelMode(false);
    setOpponentName("");
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <div className="game-container">
      <h1>üéÆ Hectoc Game</h1>

      {username && (
        <div className="user-display">
          üë§ <strong>{username}</strong>
        </div>
      )}

      {!gameStarted ? (
        <div className="game-menu">
          <div className="button-group">
            <div className="button-row">
              <button className="btn btn-primary" onClick={startGame}>
                ‚ñ∂Ô∏è Start Single Game
              </button>
              {waiting ? (
                <>
                  <button className="btn btn-warning" disabled>
                    ‚è≥ Waiting...
                  </button>
                  <button className="btn btn-secondary" onClick={cancelDuel}>
                    ‚ùå Cancel Duel
                  </button>
                </>
              ) : (
                <button className="btn btn-danger" onClick={startDuel}>
                  ‚öîÔ∏è Start Duel
                </button>
              )}
            </div>
            <div className="button-row">
              <button
                className="btn btn-info"
                onClick={() => navigate("/leaderboard")}
              >
                üìä View Leaderboard
              </button>
              <button className="btn btn-secondary" onClick={handleLogout}>
                üö™ Logout
              </button>
            </div>
          </div>
        </div>
      ) : (
        <div className="game-board">
          <h2>
            Use these numbers: <span className="digits">{digits.join(" ")}</span>
          </h2>

          {duelMode && opponentName && (
            <h4 className="text-danger">üî• Opponent: {opponentName}</h4>
          )}

          <input
            type="text"
            value={solution}
            onChange={(e) => setSolution(e.target.value)}
            placeholder="Build your solution..."
            className="solution-input"
          />

          <div className="button-group">
            <div className="button-row">
              <button className="btn btn-clear" onClick={clearSolution}>
                ‚ùå Clear
              </button>
              <button className="btn btn-success" onClick={submitSolution}>
                ‚úÖ Submit
              </button>
              <button className="btn btn-warning" onClick={returnToMenu}>
                ‚¨Ö Return to Menu
              </button>
            </div>
          </div>

          {message && (
            <div className={`message ${message.includes("Correct") || message.includes("win") ? "success" : "error"}`}>
              {message}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Game;
