import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { auth, googleProvider } from "../firebase";
import { signInWithPopup } from "firebase/auth";
import "bootstrap/dist/css/bootstrap.min.css";

const Dashboard = () => {
  const navigate = useNavigate();

  const handleGoogleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      console.log("User Signed in:", result.user);
      alert(`Welcome, ${result.user.displayName}!`);
      navigate("/news"); // Redirect to news page after login
    } catch (error) {
      console.error("Google Sign-In Error:", error);
      alert("Google Sign-In Failed!");
    }
  };

  return (
    <div className="d-flex align-items-center justify-content-center vh-100 bg-light">
      <div className="text-center p-5 shadow-lg rounded bg-white">
        <h1 className="mb-3">Welcome to Our Website! ðŸŒŸ</h1>
        <p className="lead">Join us today by signing in or creating an account.</p>
        <div className="mt-4">
          <Link to="/login" className="btn btn-primary me-3">Sign In</Link>
          <Link to="/signup" className="btn btn-success">Sign Up</Link>
        </div>
        <div className="mt-4">
          <button onClick={handleGoogleLogin} className="btn btn-danger">
            Sign in with Google ðŸ”¥
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
