import React, { useEffect, useState } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:5000"); // Update this if using a deployed server

const Leaderboard = () => {
  const [leaderboard, setLeaderboard] = useState([]);

  useEffect(() => {
    // Listen for leaderboard updates
    socket.on("leaderboardUpdate", (data) => {
      setLeaderboard(data);
    });

    // Cleanup when component unmounts
    return () => socket.off("leaderboardUpdate");
  }, []);

  return (
    <div className="leaderboard-container">
      <h2>üèÜ Leaderboard üèÜ</h2>
      <table>
        <thead>
          <tr>
            <th>Rank</th>
            <th>Username</th>
            <th>Score</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          {leaderboard.map((player, index) => (
            <tr key={player.uid}>
              <td>{index + 1}</td>
              <td>{player.username}</td>
              <td>{player.stats.classic.score}</td>
              <td>{player.stats.classic.time} sec</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Leaderboard;
