import React from "react";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const LandingPage = () => {
  return (
    <div className="container text-center" style={{ paddingTop: "10vh", color: "white", backgroundColor: "#ff4c68", height: "100vh" }}>
      <h1 style={{ fontSize: "3.5rem", fontWeight: "bold" }}>Welcome to the News Portal</h1>
      <p>Get the latest news and updates. Sign in to continue.</p>
      <Link to="/login" className="btn btn-light m-2">Sign In</Link>
      <Link to="/signup" className="btn btn-dark m-2">Sign Up</Link>
    </div>
  );
};

export default LandingPage;
