import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";

const socket = io("http://localhost:5000");

const Game = () => {
  const [digits, setDigits] = useState([]);
  const [solution, setSolution] = useState("");
  const [gameStarted, setGameStarted] = useState(false);

  useEffect(() => {
    socket.on("game-started", (data) => {
      setDigits(data);
      setGameStarted(true);
    });

    socket.on("solution-submitted", (message) => {
      alert(message);
    });
  }, []);

  const startGame = () => {
    const newDigits = Array.from({ length: 6 }, () => Math.floor(Math.random() * 9) + 1);
    setDigits(newDigits);
    socket.emit("start-game", newDigits);
  };

  const submitSolution = () => {
    socket.emit("submit-solution", `Player solution: ${solution}`);
  };

  return (
    <div className="container text-center mt-5">
      <h1>Hectoc Game</h1>
      {!gameStarted ? (
        <button className="btn btn-primary" onClick={startGame}>Start Game</button>
      ) : (
        <div>
          <h2>Use the numbers: {digits.join(" ")}</h2>
          <input
            type="text"
            className="form-control"
            placeholder="Enter your solution"
            value={solution}
            onChange={(e) => setSolution(e.target.value)}
          />
          <button className="btn btn-success mt-3" onClick={submitSolution}>
            Submit Solution
          </button>
        </div>
      )}
    </div>
  );
};

export default Game;
