import React from "react";
import { Link } from "react-router-dom";
import styles from "./LandingPage.module.css"; // Import CSS Module

const LandingPage = () => {
  return (
    <div className={styles.landingContainer}>
      <h1 className={styles.landingTitle}>Welcome to News Portal</h1>
      <p className={styles.landingText}>Stay updated with the latest news. Sign in to continue.</p>

      <div className={styles.buttonContainer}>
        <Link to="/login">
          <button className={`${styles.button} ${styles.loginBtn}`}>Sign In</button>
        </Link>
        <Link to="/signup">
          <button className={`${styles.button} ${styles.signupBtn}`}>Sign Up</button>
        </Link>
      </div>
    </div>
  );
};

export default LandingPage;
