import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const API_KEY = "YOUR_NEWS_API_KEY"; // Replace with your actual API key
const NEWS_URL = `https://newsapi.org/v2/top-headlines?country=in&apiKey=${API_KEY}`;

const News = () => {
  const [articles, setArticles] = useState([]); // ‚úÖ Default to an empty array
  const navigate = useNavigate();

  useEffect(() => {
    fetch(NEWS_URL)
      .then((response) => response.json())
      .then((data) => setArticles(data.articles || [])) // ‚úÖ Ensure it's always an array
      .catch((error) => {
        console.error("Error fetching news:", error);
        setArticles([]); // ‚úÖ Set empty array if fetch fails
      });
  }, []);

  return (
    <div className="container mt-4">
      <h2 className="mb-3">üì∞ Latest News</h2>
      <button className="btn btn-secondary mb-3" onClick={() => navigate("/")}>
        Logout
      </button>
      <div className="row">
        {articles.length > 0 ? ( // ‚úÖ Check if articles exist before mapping
          articles.map((article, index) => (
            <div key={index} className="col-md-4 mb-4">
              <div className="card">
                <img
                  src={article.urlToImage || "https://via.placeholder.com/150"}
                  className="card-img-top"
                  alt="News"
                />
                <div className="card-body">
                  <h5 className="card-title">{article.title}</h5>
                  <p className="card-text">{article.description}</p>
                  <a href={article.url} target="_blank" rel="noopener noreferrer" className="btn btn-primary">
                    Read More
                  </a>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>Loading news... ‚è≥</p> // ‚úÖ Show loading message before data arrives
        )}
      </div>
    </div>
  );
};

export default News;
