// === FRONTEND: Game.js ===
import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import { useNavigate } from "react-router-dom";
import "./Game.css";

const socket = io("http://localhost:5000");

const Game = () => {
  const [digits, setDigits] = useState([]);
  const [solution, setSolution] = useState("");
  const [gameStarted, setGameStarted] = useState(false);
  const [message, setMessage] = useState("");
  const [waiting, setWaiting] = useState(false);
  const [duelMode, setDuelMode] = useState(false);
  const [operations] = useState(["(", ")", "+", "-", "*", "/"]);
  const [username, setUsername] = useState("");
  const [opponentName, setOpponentName] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    setUsername(storedUsername || "Guest");

    socket.on("game-started", (data) => {
      setDigits(data);
      setGameStarted(true);
      setSolution("");
      setMessage("");
      setDuelMode(false);
    });

    socket.on("solution-validated", (result) => {
      setMessage(result);
    });

    socket.on("waiting-for-opponent", () => {
      setWaiting(true);
      setMessage("Waiting for an opponent...");
    });

    socket.on("duel-started", ({ digits, opponent }) => {
      setDigits(digits);
      setGameStarted(true);
      setDuelMode(true);
      setWaiting(false);
      setMessage("Duel Started! Solve the challenge first!");
      setOpponentName(opponent);
    });

    socket.on("duel-cancelled", () => {
      setWaiting(false);
      setMessage("Duel cancelled.");
    });

    socket.on("duel-result", (result) => {
      setMessage(result);
      setDuelMode(false);
    });

    return () => {
      socket.off("game-started");
      socket.off("solution-validated");
      socket.off("waiting-for-opponent");
      socket.off("duel-started");
      socket.off("duel-result");
    };
  }, []);

  const startGame = () => {
    socket.emit("start-game", { username });
  };

  const startDuel = () => {
    socket.emit("start-duel", { username });
    setWaiting(true);
    setMessage("Searching for an opponent...");
  };

  const cancelDuel = () => {
    socket.emit("cancel-duel");
    setWaiting(false);
    setMessage("Duel cancelled.");
  };

  const submitSolution = () => {
    if (!solution.trim()) {
      setMessage("Please enter a solution first!");
      return;
    }
    socket.emit("submit-solution", { solution, duelMode });
  };

  const addToSolution = (value) => {
    setSolution((prev) => prev + value);
  };

  const clearSolution = () => {
    setSolution("");
  };

  const returnToMenu = () => {
    setGameStarted(false);
    setDigits([]);
    setSolution("");
    setMessage("");
    setWaiting(false);
    setDuelMode(false);
    setOpponentName("");
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <div className="game-container">
      <h1>Hectoc Game</h1>

      {username && (
        <div className="mb-3 text-start">
          <strong>ðŸ‘¤ Logged in as:</strong>{" "}
          <span className="text-primary">{username}</span>
        </div>
      )}

      {!gameStarted ? (
        <div className="game-menu">
          <button className="btn btn-primary mb-2" onClick={startGame}>
            Start Single Game
          </button>
          {waiting ? (
            <>
              <button className="btn btn-warning mb-2" disabled>
                Waiting for opponent...
              </button>
              <button className="btn btn-secondary mb-2" onClick={cancelDuel}>
                Cancel Duel
              </button>
            </>
          ) : (
            <button className="btn btn-danger mb-2" onClick={startDuel}>
              Start Duel
            </button>
          )}

          <button className="btn btn-secondary" onClick={handleLogout}>
            Logout
          </button>
        </div>
      ) : (
        <div className="game-board">
          <h2>
            Use these numbers: <span className="digits">{digits.join(" ")}</span>
          </h2>

          {duelMode && opponentName && (
            <h4 className="text-danger">ðŸ”¥ Opponent: {opponentName}</h4>
          )}

          <div className="solution-display">
            <input
              type="text"
              value={solution}
              onChange={(e) => setSolution(e.target.value)}
              placeholder="Build your solution..."
              className="solution-input"
            />
            <button className="btn btn-clear" onClick={clearSolution}>
              Clear
            </button>
          </div>

          <div className="operations-panel">
            {operations.map((op) => (
              <button
                key={op}
                className="btn btn-operation"
                onClick={() => addToSolution(op)}
              >
                {op}
              </button>
            ))}
          </div>

          <div className="digits-panel">
            {digits.map((digit, index) => (
              <button
                key={index}
                className="btn btn-digit"
                onClick={() => addToSolution(digit.toString())}
              >
                {digit}
              </button>
            ))}
          </div>

          <div className="d-flex flex-column align-items-center gap-2 mt-3">
            <button className="btn btn-success" onClick={submitSolution}>
              Submit Solution
            </button>

            <button className="btn btn-warning mt-2" onClick={returnToMenu}>
              â¬… Return to Menu
            </button>
          </div>

          {message && (
            <div
              className={`message ${
                message.includes("Correct") || message.includes("win")
                  ? "success"
                  : "error"
              }`}
            >
              {message}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Game;


// === BACKEND (duel-started emit logic) ===
// Inside your duel pairing logic (usually in socket.on('start-duel')):

// Example in server.js or gameHandlers.js:
const player1 = waitingPlayers.shift();
const player2 = socket;

const digits = generatePuzzle(); // function that returns the number set

player1.emit("duel-started", {
  digits,
  opponent: player2.username || "Guest",
});

player2.emit("duel-started", {
  digits,
  opponent: player1.username || "Guest",
});
