import React from "react";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const Dashboard = () => {
  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
      <div className="text-center p-5 shadow-lg bg-white rounded" style={{ maxWidth: "400px" }}>
        <h1 className="mb-4 text-primary">Welcome!</h1>
        <p className="text-muted">Sign in or create an account to access the latest news.</p>
        <Link to="/login" className="btn btn-primary btn-lg w-100 mb-3">Login</Link>
        <Link to="/signup" className="btn btn-success btn-lg w-100">Sign Up</Link>
      </div>
    </div>
  );
};

export default Dashboard;
