import React, { useEffect, useState } from "react";
import io from "socket.io-client";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";
const socket = io(API_URL, { transports: ["websocket"] });

const Leaderboard = () => {
  const [leaderboard, setLeaderboard] = useState([]);

  useEffect(() => {
    // Fetch initial leaderboard
    fetch(`${API_URL}/leaderboard/classic`)
      .then((res) => res.json())
      .then((data) => setLeaderboard(data.leaderboard))
      .catch((error) => console.error("Error fetching leaderboard:", error));

    // Listen for real-time leaderboard updates
    socket.on("leaderboardUpdate", (data) => {
      setLeaderboard((prevLeaderboard) =>
        data.map((player, index) => ({
          ...player,
          rank: index + 1, // Ensure ranking updates dynamically
        }))
      );
    });

    socket.on("connect_error", (err) => {
      console.error("Socket connection error:", err);
    });

    return () => socket.off("leaderboardUpdate");
  }, []);

  return (
    <div className="leaderboard-container">
      <h2>üèÜ Leaderboard üèÜ</h2>
      <table>
        <thead>
          <tr>
            <th>Rank</th>
            <th>Username</th>
            <th>Score</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          {leaderboard.length === 0 ? (
            <tr>
              <td colSpan="4" className="text-center">No data available</td>
            </tr>
          ) : (
            leaderboard.map((player) => (
              <tr key={player.username}>
                <td>{player.rank}</td>
                <td>{player.username}</td>
                <td>{player.stats.classic.score}</td>
                <td>{player.stats.classic.time} sec</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Leaderboard;
