// AuthForm.js
import React from "react";
import { Link } from "react-router-dom";
import "./AuthStyles.css"; // Import the new CSS file

const AuthForm = ({ isSignup }) => {
  return (
    <div className="auth-container">
      <div className="auth-form">
        <h1 className="auth-title">{isSignup ? "Create Account" : "Welcome Back"}</h1>
        <p className="auth-subtitle">
          {isSignup ? "Join our community today" : "Sign in to continue"}
        </p>
        
        <form>
          <div className="form-group">
            <label className="form-label">Email</label>
            <input 
              type="email" 
              className="form-input" 
              placeholder="Enter your email"
            />
          </div>
          
          <div className="form-group">
            <label className="form-label">Password</label>
            <input 
              type="password" 
              className="form-input" 
              placeholder="Enter your password"
            />
          </div>
          
          {isSignup && (
            <div className="form-group">
              <label className="form-label">Confirm Password</label>
              <input 
                type="password" 
                className="form-input" 
                placeholder="Confirm your password"
              />
            </div>
          )}
          
          <button 
            type="submit" 
            className={`auth-button ${isSignup ? "signup-button" : "login-button"}`}
          >
            {isSignup ? "Sign Up" : "Login"}
          </button>
        </form>
        
        <div className="auth-footer">
          {isSignup ? (
            <p>Already have an account? <Link to="/login" className="auth-link">Login</Link></p>
          ) : (
            <p>Don't have an account? <Link to="/signup" className="auth-link">Sign Up</Link></p>
          )}
        </div>
      </div>
    </div>
  );
};

export default AuthForm;
