import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { signInWithEmailAndPassword, createUserWithEmailAndPassword, GoogleAuthProvider, signInWithPopup } from "firebase/auth";
import { auth } from "../firebase";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Auth.css";



const AuthForm = ({ isSignup }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let userCredential;
      if (isSignup) {
        userCredential = await createUserWithEmailAndPassword(auth, email, password);
      } else {
        userCredential = await signInWithEmailAndPassword(auth, email, password);
      }
  
      const user = userCredential.user;
  
      // Send user data to the backend
      await fetch("http://localhost:5000/api/auth/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          uid: user.uid,
          email: user.email,
          displayName: user.displayName || email.split("@")[0],
          username: email.split("@")[0], // Generate username if missing
        }),
      });
  
      navigate(isSignup ? "/login" : "/game");
    } catch (err) {
      setError("Authentication failed! Try again.");
    }
  };
  
  const handleGoogleLogin = async () => {
    const provider = new GoogleAuthProvider();
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
  
      // Send user data to the backend
      await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ token: await user.getIdToken() }),
      });
  
      navigate("/game");
    } catch (err) {
      setError("Google Sign-In failed! Try again.");
    }
  };
  
  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
      <div className="p-4 shadow-lg bg-white rounded" style={{ maxWidth: "400px", width: "100%" }}>
        <h2 className="text-center text-primary mb-3">{isSignup ? "Sign Up" : "Login"}</h2>
        {error && <p className="text-danger text-center">{error}</p>}
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Email:</label>
            <input type="email" className="form-control" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>
          <div className="mb-3">
            <label className="form-label">Password:</label>
            <input type="password" className="form-control" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </div>
          <button type="submit" className="btn btn-primary w-100 mb-2">{isSignup ? "Sign Up" : "Login"}</button>
        </form>
        <button onClick={handleGoogleLogin} className="btn btn-danger w-100 mb-3">Sign in with Google</button>
        <p className="mt-3 text-center">
          {isSignup ? "Already a user? " : "New user? "}
          <a href={isSignup ? "/login" : "/signup"} className="text-decoration-none text-primary">
            {isSignup ? "Login" : "Sign up"}
          </a>
        </p>
      </div>
    </div>
  );
};

export default AuthForm;
