import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";

const socket = io("http://localhost:5000");

const Game = () => {
  const [digits, setDigits] = useState([]);
  const [solution, setSolution] = useState("");
  const [gameStarted, setGameStarted] = useState(false);
  const [message, setMessage] = useState("");
  const [waiting, setWaiting] = useState(false);
  const [duelMode, setDuelMode] = useState(false);
  const [email, setEmail] = useState(localStorage.getItem("email") || "");

  useEffect(() => {
    socket.on("game-started", (data) => {
      setDigits(data);
      setGameStarted(true);
      setSolution("");
      setMessage("");
      setDuelMode(false);
    });

    socket.on("solution-validated", (result) => {
      setMessage(result);
    });

    socket.on("waiting-for-opponent", () => {
      setWaiting(true);
      setMessage("Waiting for an opponent...");
    });

    socket.on("duel-started", ({ digits }) => {
      setDigits(digits);
      setGameStarted(true);
      setDuelMode(true);
      setWaiting(false);
      setMessage("Duel Started! Solve the challenge first!");
    });

    socket.on("duel-result", (result) => {
      setMessage(result);
      setDuelMode(false);
    });

    return () => {
      socket.off("game-started");
      socket.off("solution-validated");
      socket.off("waiting-for-opponent");
      socket.off("duel-started");
      socket.off("duel-result");
    };
  }, []);

  const handleSaveEmail = () => {
    if (email.trim()) {
      localStorage.setItem("email", email);
      socket.emit("register-email", { email });
    }
  };

  const startGame = () => {
    if (!email) {
      alert("Please enter your email first!");
      return;
    }
    socket.emit("start-game", { email });
  };

  const startDuel = () => {
    if (!email) {
      alert("Please enter your email first!");
      return;
    }
    socket.emit("start-duel", { email });
    setWaiting(true);
    setMessage("Searching for an opponent...");
  };

  const submitSolution = () => {
    if (!solution.trim()) {
      alert("Enter a solution first!");
      return;
    }
    socket.emit("submit-solution", { email, solution, duelMode });
  };

  return (
    <div className="container text-center mt-5">
      <h1>Hectoc Game</h1>

      {!email ? (
        <div>
          <input
            type="email"
            className="form-control"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button className="btn btn-primary mt-3" onClick={handleSaveEmail}>
            Save Email
          </button>
        </div>
      ) : !gameStarted ? (
        <div>
          <button className="btn btn-primary btn-lg m-2" onClick={startGame}>
            Start Single Game
          </button>
          <button className="btn btn-danger btn-lg m-2" onClick={startDuel} disabled={waiting}>
            {waiting ? "Waiting..." : "Start Duel"}
          </button>
        </div>
      ) : (
        <div>
          <h2 className="mt-4">
            Use the numbers: <strong>{digits.join(" ")}</strong>
          </h2>
          <input
            type="text"
            className="form-control mt-3"
            placeholder="Enter your solution..."
            value={solution}
            onChange={(e) => setSolution(e.target.value)}
          />
          <button className="btn btn-success mt-3" onClick={submitSolution}>
            Submit Solution
          </button>
          {message && (
            <p className="mt-3">
              <strong>{message}</strong>
            </p>
          )}
        </div>
      )}
    </div>
  );
};

export default Game;
