import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";

const socket = io("http://localhost:5000");

const Game = () => {
  const [digits, setDigits] = useState([]);
  const [solution, setSolution] = useState("");
  const [gameStarted, setGameStarted] = useState(false);
  const [message, setMessage] = useState("");

  useEffect(() => {
    socket.on("game-started", (data) => {
      setDigits(data);
      setGameStarted(true);
      setMessage(""); // Reset message when new game starts
    });

    socket.on("solution-validated", (result) => {
      setMessage(result);
    });

    return () => {
      socket.off("game-started");
      socket.off("solution-validated");
    };
  }, []);

  const startGame = () => {
    const newDigits = Array.from({ length: 6 }, () => Math.floor(Math.random() * 9) + 1);
    setDigits(newDigits);
    setGameStarted(true);
    setMessage("");
    socket.emit("start-game", newDigits);
  };

  const submitSolution = () => {
    if (!solution.trim()) {
      alert("Enter a solution first!");
      return;
    }
    socket.emit("submit-solution", { solution, digits });
  };

  return (
    <div className="container text-center mt-5">
      <h1>Hectoc Game</h1>
      {!gameStarted ? (
        <button className="btn btn-primary btn-lg" onClick={startGame}>
          Start Game
        </button>
      ) : (
        <div>
          <h2 className="mt-4">Use the numbers: <strong>{digits.join(" ")}</strong></h2>
          <input
            type="text"
            className="form-control mt-3"
            placeholder="Enter your solution..."
            value={solution}
            onChange={(e) => setSolution(e.target.value)}
          />
          <button className="btn btn-success mt-3" onClick={submitSolution}>
            Submit Solution
          </button>
          {message && <p className="mt-3"><strong>{message}</strong></p>}
        </div>
      )}
    </div>
  );
};

export default Game;
