import React, { useState, useEffect } from "react";
import { auth } from "../firebase";
import { createUserWithEmailAndPassword } from "firebase/auth";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const Signup = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [dateTime, setDateTime] = useState(new Date().toLocaleString());

  // Update date and time every second
  useEffect(() => {
    const interval = setInterval(() => {
      setDateTime(new Date().toLocaleString());
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      // Create user in Firebase
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      const token = await user.getIdToken();

      // Send user data to backend
      await axios.post("http://localhost:5000/api/auth/signup", {
        uid: user.uid,
        email: user.email,
        displayName: name,
      });

      alert("Signup successful! Please log in.");
    } catch (error) {
      alert("Signup failed: " + error.message);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Signup</h2>
      <p>{dateTime}</p>
      <form onSubmit={handleSignup}>
        <input className="form-control mb-2" type="text" placeholder="Full Name" value={name} onChange={(e) => setName(e.target.value)} required />
        <input className="form-control mb-2" type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input className="form-control mb-2" type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <button className="btn btn-success" type="submit">Signup</button>
      </form>
    </div>
  );
};

export default Signup;
