import React, { useEffect, useState } from "react";
import io from "socket.io-client";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";
const socket = io(API_URL, { transports: ["websocket"] });

const Leaderboard = () => {
  const [leaderboard, setLeaderboard] = useState([]);
  const [mode, setMode] = useState("classic");

  useEffect(() => {
    fetch(`${API_URL}/leaderboard/${mode}`)
      .then((res) => res.json())
      .then((data) => setLeaderboard(data.leaderboard))
      .catch((error) => console.error("Error fetching leaderboard:", error));
  }, [mode]);

  useEffect(() => {
    const handler = (data) => {
      setLeaderboard(
        data
          .filter((user) => user.stats[mode]) // Only show users with that mode data
          .map((player, index) => ({
            ...player,
            rank: index + 1,
          }))
      );
    };

    socket.on("leaderboardUpdate", handler);
    socket.on("connect_error", (err) => {
      console.error("Socket connection error:", err);
    });

    return () => {
      socket.off("leaderboardUpdate", handler);
    };
  }, [mode]);

  return (
    <div className="leaderboard-container">
      <h2>üèÜ {mode === "classic" ? "Classic Mode" : "Duel Mode"} Leaderboard üèÜ</h2>

      <div style={{ marginBottom: "1rem" }}>
        <button onClick={() => setMode("classic")}>Classic</button>
        <button onClick={() => setMode("duel")}>Duel</button>
      </div>

      <table>
        <thead>
          <tr>
            <th>Rank</th>
            <th>Username</th>
            <th>Score</th>
            <th>Best Time</th>
          </tr>
        </thead>
        <tbody>
          {leaderboard.length === 0 ? (
            <tr>
              <td colSpan="4" className="text-center">No data available</td>
            </tr>
          ) : (
            leaderboard.map((player) => (
              <tr key={player.username}>
                <td>{player.rank}</td>
                <td>{player.username}</td>
                <td>{player.stats[mode].score}</td>
                <td>{player.stats[mode].time} sec</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Leaderboard;
