import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

const API_KEY = "pub_7774596fc4668b66986fdb9024d9e6831154c"; // ‚úÖ Your API key
const NEWS_URL = `https://newsdata.io/api/1/latest?apikey=${API_KEY}&language=en`; // Fetch latest English news

const News = () => {
  const [articles, setArticles] = useState([]); // ‚úÖ Default to empty array
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(NEWS_URL)
      .then((response) => response.json())
      .then((data) => {
        setArticles(data.results || []); // ‚úÖ Ensure data is an array
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching news:", error);
        setArticles([]);
        setLoading(false);
      });
  }, []);

  return (
    <div className="container mt-4">
      <h2 className="mb-3">üì∞ Latest News</h2>
      <button className="btn btn-secondary mb-3" onClick={() => navigate("/")}>
        Logout
      </button>

      {loading ? (
        <p>Loading news... ‚è≥</p> // ‚úÖ Show loading message while fetching data
      ) : articles.length > 0 ? (
        <div className="row">
          {articles.map((article, index) => (
            <div key={index} className="col-md-4 mb-4">
              <div className="card">
                <img
                  src={article.image_url || "https://via.placeholder.com/150"}
                  className="card-img-top"
                  alt="News"
                />
                <div className="card-body">
                  <h5 className="card-title">{article.title}</h5>
                  <p className="card-text">{article.description}</p>
                  <a href={article.link} target="_blank" rel="noopener noreferrer" className="btn btn-primary">
                    Read More
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No news available üòï</p> // ‚úÖ Handle empty news results
      )}
    </div>
  );
};

export default News;
